import React, { useState } from 'react';
import axios from 'axios';
import { API_URL } from '../api';

export default function Registro() {
  const [form, setForm] = useState({
    nombre: '',
    cedula: '',
    email: '',
    cargo: '',
    entidad: ''
  });
  const [qr, setQr] = useState(null);
  const [mensaje, setMensaje] = useState('');

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMensaje('');
    setQr(null);
    try {
      const { data } = await axios.post(`${API_URL}/inscripcion`, form);
      setQr(data.qrCode);
      setMensaje(data.mensaje || 'Inscripción registrada');
    } catch (err) {
      console.error(err);
      setMensaje(err.response?.data?.error || 'Error en la inscripción');
    }
  };

  return (
    <div>
      <h2>Registro al Evento</h2>
      <form onSubmit={handleSubmit}>
        <input name="nombre" placeholder="Nombre completo" value={form.nombre} onChange={handleChange} required />
        <input name="cedula" placeholder="Cédula" value={form.cedula} onChange={handleChange} required />
        <input name="email" placeholder="Email" type="email" value={form.email} onChange={handleChange} required />
        <input name="cargo" placeholder="Cargo / Rol" value={form.cargo} onChange={handleChange} />
        <input name="entidad" placeholder="Entidad / Empresa" value={form.entidad} onChange={handleChange} />
        <button type="submit">Registrar</button>
      </form>

      {mensaje && <p><strong>{mensaje}</strong></p>}

      {qr && (
        <div>
          <h3>QR generado (descarga o guarda imagen):</h3>
          <img
            src={qrCode}
            alt="QR de confirmación"
            style={{
              width: '250px',   // Aumenta el tamaño
              height: '250px',  // Mantiene proporción
              border: '2px solid #ccc',
              borderRadius: '10px',
              padding: '10px',
              backgroundColor: 'white'
            }}
          />
          <button
            onClick={() => {
              const link = document.createElement('a');
              link.href = qrCode;
              link.download = 'QR_confirmacion.png';
              link.click();
            }}
            style={{
                marginTop: '20px',
                padding: '10px 20px',
                fontSize: '16px',
                backgroundColor: '#007bff',
                color: 'white',
                border: 'none',
                borderRadius: '8px',
                cursor: 'pointer'
              }}
            >
            📥 Descargar QR
          </button>
        </div>
      )}
    </div>
  );
}
