import React, { useState, useRef } from "react";
import { QrReader } from "react-qr-reader";
import axios from "axios";
import { API_URL } from "../api";
import logo from "../assets/logo.png"; // ‚úÖ aseg√∫rate de tener este archivo

export default function Confirmacion() {
  const [mensaje, setMensaje] = useState("");
  const [tipoMensaje, setTipoMensaje] = useState("");
  const [autenticado, setAutenticado] = useState(false);
  const [credenciales, setCredenciales] = useState({ usuario: "", contrasena: "" });
  const manualRef = useRef();
  const ultimoEscaneo = useRef("");
  const bloqueado = useRef(false);

  const playFeedback = (success = true) => {
    const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
    const oscillator = audioCtx.createOscillator();
    const gainNode = audioCtx.createGain();
    oscillator.connect(gainNode);
    gainNode.connect(audioCtx.destination);
    oscillator.type = success ? "sine" : "square";
    oscillator.frequency.setValueAtTime(success ? 880 : 220, audioCtx.currentTime);
    gainNode.gain.setValueAtTime(0.1, audioCtx.currentTime);
    oscillator.start();
    oscillator.stop(audioCtx.currentTime + 0.2);
    if (navigator.vibrate) navigator.vibrate(success ? [80, 30, 80] : [250]);
  };

  const login = async () => {
    if (!credenciales.usuario || !credenciales.contrasena) {
      setMensaje("‚ö†Ô∏è Debes ingresar usuario y contrase√±a");
      setTipoMensaje("error");
      return;
    }

    try {
      const cred = btoa(`${credenciales.usuario}:${credenciales.contrasena}`);
      await axios.post(`${API_URL}/login`, {}, { headers: { Authorization: `Basic ${cred}` } });
      setAutenticado(true);
      setMensaje("");
    } catch (error) {
      setTipoMensaje("error");
      if (error.response?.status === 401) {
        setMensaje("‚ùå Usuario o contrase√±a incorrectos");
      } else {
        setMensaje("‚ö†Ô∏è Error de conexi√≥n con el servidor");
      }
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") login();
  };

  async function confirmar(cedula) {
    if (!cedula || bloqueado.current) return;

    bloqueado.current = true;
    setMensaje("");

    try {
      const cred = btoa(`${credenciales.usuario}:${credenciales.contrasena}`);
      const { data } = await axios.post(
        `${API_URL}/confirmar`,
        { cedula },
        { headers: { Authorization: `Basic ${cred}` } }
      );

      playFeedback(true);
      setTipoMensaje("exito");
      setMensaje(data.mensaje || "‚úÖ Asistencia confirmada");

      setTimeout(() => setMensaje(""), 800);
    } catch (err) {
      playFeedback(false);
      setTipoMensaje("error");
      if (err.response?.status === 404) setMensaje("‚ùå No se encontr√≥ la inscripci√≥n");
      else setMensaje("‚ö†Ô∏è Error confirmando asistencia");
    } finally {
      setTimeout(() => {
        bloqueado.current = false;
        ultimoEscaneo.current = "";
      }, 1000);
    }
  }

  const handleScan = (data) => {
    if (data?.text) {
      const texto = data.text.trim();
      if (texto === ultimoEscaneo.current) return;
      ultimoEscaneo.current = texto;

      const cedula = texto.replace("cedula:", "").trim();
      confirmar(cedula);
    }
  };

  const handleError = (err) => console.error("Error QR:", err);

  // üü¢ Login UI
  if (!autenticado) {
    return (
      <div style={{ padding: 0, textAlign: "center" }}>
        {/* üü¢ Banner con el logo */}
        <div style={{ width: "100%", backgroundColor: "#f5f5f5" }}>
          <img
            src={logo}
            alt="Logo"
            style={{
              width: "100%",
              maxHeight: "120px",
              objectFit: "cover",
              display: "block",
            }}
          />
        </div>

        <div style={{ padding: 20, maxWidth: 400, margin: "0 auto" }}>
          <h2>Ingreso de Usuario</h2>
          <input
            placeholder="Usuario"
            value={credenciales.usuario}
            onChange={(e) =>
              setCredenciales({ ...credenciales, usuario: e.target.value })
            }
            onKeyDown={handleKeyDown}
            style={{ width: "100%", padding: 8, marginBottom: 10 }}
          />
          <input
            type="password"
            placeholder="Contrase√±a"
            value={credenciales.contrasena}
            onChange={(e) =>
              setCredenciales({ ...credenciales, contrasena: e.target.value })
            }
            onKeyDown={handleKeyDown}
            style={{ width: "100%", padding: 8, marginBottom: 10 }}
          />
          <button
            onClick={login}
            style={{
              width: "100%",
              padding: "10px 12px",
              backgroundColor: "#007bff",
              color: "white",
              border: "none",
              borderRadius: "8px",
              cursor: "pointer",
            }}
          >
            Ingresar
          </button>
          {mensaje && <p style={{ color: "red", marginTop: 10 }}>{mensaje}</p>}
        </div>
      </div>
    );
  }

  // üü¢ Vista principal (ya autenticado)
  return (
    <div style={{ padding: 0, textAlign: "center" }}>
      {/* üü¢ Banner superior */}
      <div style={{ width: "100%", backgroundColor: "#f5f5f5" }}>
        <img
          src={logo}
          alt="Logo"
          style={{
            width: "100%",
            maxHeight: "120px",
            objectFit: "cover",
            display: "block",
          }}
        />
      </div>

      <div style={{ padding: 20 }}>
        <h2>Confirmar Asistencia</h2>
        <p>Escanea el QR o ingresa la c√©dula manualmente.</p>

        <div style={{ maxWidth: 400, margin: "0 auto" }}>
          <QrReader
            constraints={{ facingMode: "environment" }}
            onResult={(result, error) => {
              if (!!result) handleScan(result);
              if (!!error) handleError(error);
            }}
            style={{ width: "100%" }}
          />
        </div>

        <div style={{ marginTop: 20 }}>
          <input
            placeholder="C√©dula (manual)"
            ref={manualRef}
            style={{ padding: 6, marginRight: 10 }}
          />
          <button
            onClick={() => confirmar(manualRef.current.value)}
            style={{ padding: "6px 12px" }}
          >
            Confirmar manual
          </button>
        </div>

        {/* üü¢ Modal con bot√≥n cerrar */}
        {mensaje && (
          <div
            style={{
              position: "fixed",
              top: 0,
              left: 0,
              right: 0,
              bottom: 0,
              backgroundColor: "rgba(0,0,0,0.5)",
              display: "flex",
              alignItems: "center",
              justifyContent: "center",
              zIndex: 9999,
            }}
          >
            <div
              style={{
                backgroundColor: "white",
                borderRadius: 16,
                padding: "40px 60px",
                textAlign: "center",
                boxShadow: "0 4px 20px rgba(0,0,0,0.3)",
              }}
            >
              <div style={{ fontSize: 80 }}>
                {tipoMensaje === "exito" ? "‚úÖ" : "‚ùå"}
              </div>
              <h3
                style={{
                  marginTop: 10,
                  color: tipoMensaje === "exito" ? "#28a745" : "#dc3545",
                }}
              >
                {mensaje}
              </h3>
              <button
                onClick={() => {
                  setMensaje("");
                  setTipoMensaje("");
                }}
                style={{
                  marginTop: 20,
                  padding: "10px 20px",
                  borderRadius: 10,
                  backgroundColor: "#007bff",
                  color: "white",
                  border: "none",
                  fontWeight: "bold",
                  cursor: "pointer",
                  boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
                }}
              >
                Cerrar
              </button>
            </div>
          </div>
        )}
      </div>
    </div>
  );
}
