import React, { useState, useRef } from 'react';
import QrScanner from 'react-qr-scanner';
import axios from 'axios';
import { API_URL } from '../api';

export default function Confirmacion() {
  const [mensaje, setMensaje] = useState('');
  const [scanning, setScanning] = useState(true);
  const [autenticado, setAutenticado] = useState(false);
  const [credenciales, setCredenciales] = useState({ usuario: '', contrasena: '' });
  const manualRef = useRef();

  // 🟢 Escaneo del QR
  const handleScan = async (data) => {
    if (data && data.text) {
      const cedula = data.text.replace('cedula:', '').trim();
      await confirmar(cedula);
      setScanning(false);
    }
  };

  const handleError = (err) => {
    console.error('Error al escanear:', err);
  };

  // 🟢 Validar autenticación local (solo muestra vista si ok)
  const login = () => {
    if (credenciales.usuario.trim() && credenciales.contrasena.trim()) {
      setAutenticado(true);
      setMensaje('');
    } else {
      setMensaje('⚠️ Debes ingresar usuario y contraseña');
    }
  };

  // 🟢 Confirmar asistencia
  async function confirmar(cedula) {
    if (!cedula) return;
    setMensaje('Procesando...');
    try {
      const cred = btoa(`${credenciales.usuario}:${credenciales.contrasena}`);
      const { data } = await axios.post(
        `${API_URL}/confirmar`,
        { cedula },
        {
          headers: {
            'Authorization': `Basic ${cred}`,
          },
        }
      );
      setMensaje(data.mensaje || '✅ Asistencia confirmada');
    } catch (err) {
      console.error(err);
      setMensaje(err.response?.data?.error || '❌ Error confirmando asistencia');
    }
  }

  // 🟢 Mostrar login si no está autenticado
  if (!autenticado) {
    return (
      <div style={{ padding: 20, textAlign: 'center', maxWidth: 400, margin: '0 auto' }}>
        <h2>Ingreso de Usuario</h2>
        <p>Ingresa tus credenciales para confirmar asistencia.</p>
        <input
          placeholder="Usuario"
          value={credenciales.usuario}
          onChange={(e) => setCredenciales({ ...credenciales, usuario: e.target.value })}
          style={{ width: '100%', padding: 6, marginBottom: 10 }}
        />
        <input
          placeholder="Contraseña"
          type="password"
          value={credenciales.contrasena}
          onChange={(e) => setCredenciales({ ...credenciales, contrasena: e.target.value })}
          style={{ width: '100%', padding: 6, marginBottom: 10 }}
        />
        <button
          onClick={login}
          style={{
            width: '100%',
            padding: '8px 12px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
          }}
        >
          Ingresar
        </button>
        {mensaje && <p style={{ color: 'red', marginTop: 10 }}>{mensaje}</p>}
      </div>
    );
  }

  // 🟢 Si ya está autenticado, muestra el escáner
  return (
    <div style={{ padding: 20, textAlign: 'center' }}>
      <h2>Confirmar Asistencia</h2>
      <p>Escanea el código QR del participante o introduce la cédula manualmente.</p>

      {scanning && (
        <div style={{ maxWidth: 400, margin: '0 auto' }}>
          <QrScanner
            delay={300}
            onError={handleError}
            onScan={handleScan}
            style={{ width: '100%' }}
          />
        </div>
      )}

      <div style={{ marginTop: 20 }}>
        <input
          placeholder="Cédula (manual)"
          ref={manualRef}
          style={{ padding: 6, marginRight: 10 }}
        />
        <button
          onClick={() => confirmar(manualRef.current.value)}
          style={{ padding: '6px 12px', marginRight: 8 }}
        >
          Confirmar manual
        </button>
        <button
          onClick={() => {
            setScanning((s) => !s);
            setMensaje('');
          }}
          style={{ padding: '6px 12px' }}
        >
          {scanning ? '⏸️ Pausar escaneo' : '▶️ Reanudar escaneo'}
        </button>
      </div>

      {mensaje && (
        <p style={{ marginTop: 20, fontWeight: 'bold' }}>
          {mensaje}
        </p>
      )}
    </div>
  );
}
