import React, { useState, useRef } from 'react';
import QrScanner from 'react-qr-scanner';
import axios from 'axios';
import { API_URL } from '../api';

export default function Confirmacion() {
  const [mensaje, setMensaje] = useState('');
  const [tipoMensaje, setTipoMensaje] = useState('');
  const [scanning, setScanning] = useState(true);
  const [autenticado, setAutenticado] = useState(false);
  const [credenciales, setCredenciales] = useState({ usuario: '', contrasena: '' });
  const manualRef = useRef();
  const ultimoEscaneo = useRef('');

  // üü¢ Escaneo QR continuo
  const handleScan = async (data) => {
    if (data && data.text) {
      const texto = data.text.trim();
      if (texto === ultimoEscaneo.current) return; // evita duplicados inmediatos
      ultimoEscaneo.current = texto;

      const cedula = texto.replace('cedula:', '').trim();
      await confirmar(cedula);
    }
  };

  const handleError = (err) => console.error('Error al escanear:', err);

  // üü¢ Login
  const login = async () => {
    if (!credenciales.usuario || !credenciales.contrasena) {
      setMensaje('‚ö†Ô∏è Debes ingresar usuario y contrase√±a');
      return;
    }

    try {
      const cred = btoa(`${credenciales.usuario}:${credenciales.contrasena}`);
      await axios.post(`${API_URL}/login`, {}, {
        headers: { Authorization: `Basic ${cred}` }
      });
      setAutenticado(true);
      setMensaje('');
    } catch (error) {
      if (error.response?.status === 401) {
        setMensaje('‚ùå Usuario o contrase√±a incorrectos');
      } else {
        setMensaje('‚ö†Ô∏è Error de conexi√≥n con el servidor');
      }
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter') login();
  };

  // üîà Feedback con sonido + vibraci√≥n
  const playFeedback = (success = true) => {
    const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
    const oscillator = audioCtx.createOscillator();
    const gainNode = audioCtx.createGain();
    oscillator.connect(gainNode);
    gainNode.connect(audioCtx.destination);
    oscillator.type = success ? 'sine' : 'square';
    oscillator.frequency.setValueAtTime(success ? 880 : 220, audioCtx.currentTime);
    gainNode.gain.setValueAtTime(0.1, audioCtx.currentTime);
    oscillator.start();
    oscillator.stop(audioCtx.currentTime + 0.2);
    if (navigator.vibrate) navigator.vibrate(success ? [100, 50, 100] : [300]);
  };

  // üü¢ Confirmar asistencia
  async function confirmar(cedula) {
    if (!cedula) return;

    try {
      const cred = btoa(`${credenciales.usuario}:${credenciales.contrasena}`);
      const { data } = await axios.post(
        `${API_URL}/confirmar`,
        { cedula },
        { headers: { Authorization: `Basic ${cred}` } }
      );

      // ‚úÖ √âxito: alerta r√°pida y continuar escaneando
      playFeedback(true);
      setTipoMensaje('exito');
      setMensaje(data.mensaje || 'Asistencia confirmada');
      setTimeout(() => setMensaje(''), 600);

      // limpia duplicado tras un segundo
      setTimeout(() => (ultimoEscaneo.current = ''), 1000);
    } catch (err) {
      console.error(err);

      // ‚ö†Ô∏è Si el backend responde 404, es que no existe la c√©dula ‚Üí error real
      if (err.response?.status === 404) {
        playFeedback(false);
        setTipoMensaje('error');
        setMensaje('‚ùå No se encontr√≥ la inscripci√≥n');
        setScanning(false); // se pausa solo en error real
        return;
      }

      // ‚ö†Ô∏è Si ya estaba confirmada o cualquier mensaje de estado no cr√≠tico
      if (err.response?.data?.mensaje?.includes('ya') || err.response?.data?.error?.includes('ya')) {
        playFeedback(true);
        setTipoMensaje('exito');
        setMensaje('‚úÖ Ya estaba confirmada');
        setTimeout(() => setMensaje(''), 600);
        setTimeout(() => (ultimoEscaneo.current = ''), 1000);
        return;
      }

      // üö® Cualquier otro error (servidor, red, etc.)
      playFeedback(false);
      setTipoMensaje('error');
      setMensaje('Error confirmando asistencia');
      setScanning(false);
    }
  }

  // üü¢ Vista de login
  if (!autenticado) {
    return (
      <div style={{ padding: 20, textAlign: 'center', maxWidth: 400, margin: '0 auto' }}>
        <h2>Ingreso de Usuario</h2>
        <input
          placeholder="Usuario"
          value={credenciales.usuario}
          onChange={(e) => setCredenciales({ ...credenciales, usuario: e.target.value })}
          onKeyDown={handleKeyDown}
          style={{ width: '100%', padding: 8, marginBottom: 10 }}
        />
        <input
          type="password"
          placeholder="Contrase√±a"
          value={credenciales.contrasena}
          onChange={(e) => setCredenciales({ ...credenciales, contrasena: e.target.value })}
          onKeyDown={handleKeyDown}
          style={{ width: '100%', padding: 8, marginBottom: 10 }}
        />
        <button
          onClick={login}
          style={{
            width: '100%',
            padding: '10px 12px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
          }}
        >
          Ingresar
        </button>
        {mensaje && <p style={{ color: 'red', marginTop: 10 }}>{mensaje}</p>}
      </div>
    );
  }

  // üü¢ Vista principal
  return (
    <div style={{ padding: 20, textAlign: 'center' }}>
      <h2>Confirmar Asistencia</h2>
      <p>Escanea el QR o ingresa la c√©dula manualmente.</p>

      {scanning && (
        <div style={{ maxWidth: 400, margin: '0 auto' }}>
          <QrScanner delay={100} onError={handleError} onScan={handleScan} style={{ width: '100%' }} />
        </div>
      )}

      <div style={{ marginTop: 20 }}>
        <input placeholder="C√©dula (manual)" ref={manualRef} style={{ padding: 6, marginRight: 10 }} />
        <button onClick={() => confirmar(manualRef.current.value)} style={{ padding: '6px 12px', marginRight: 8 }}>
          Confirmar manual
        </button>
        <button
          onClick={() => {
            setScanning((s) => !s);
            setMensaje('');
          }}
          style={{ padding: '6px 12px' }}
        >
          {scanning ? '‚è∏Ô∏è Pausar escaneo' : '‚ñ∂Ô∏è Reanudar escaneo'}
        </button>
      </div>

      {/* üü¢ Modal centrado */}
      {mensaje && (
        <div
          style={{
            position: 'fixed',
            top: 0,
            left: 0,
            right: 0,
            bottom: 0,
            backgroundColor: 'rgba(0, 0, 0, 0.5)',
            display: 'flex',
            alignItems: 'center',
            justifyContent: 'center',
            zIndex: 9999,
          }}
        >
          <div
            style={{
              backgroundColor: 'white',
              borderRadius: 16,
              padding: '40px 60px',
              textAlign: 'center',
              boxShadow: '0 4px 20px rgba(0,0,0,0.3)',
            }}
          >
            <div style={{ fontSize: 80 }}>{tipoMensaje === 'exito' ? '‚úÖ' : '‚ùå'}</div>
            <h3 style={{ marginTop: 10, color: tipoMensaje === 'exito' ? '#28a745' : '#dc3545' }}>
              {mensaje}
            </h3>
            {tipoMensaje === 'error' && (
              <button
                onClick={() => {
                  setMensaje('');
                  setScanning(true);
                }}
                style={{
                  marginTop: 20,
                  padding: '8px 16px',
                  borderRadius: 8,
                  backgroundColor: '#007bff',
                  color: 'white',
                  border: 'none',
                  cursor: 'pointer',
                }}
              >
                Cerrar y reanudar escaneo
              </button>
            )}
          </div>
        </div>
      )}
    </div>
  );
}
