import React, { useState, useRef } from 'react';
import QrScanner from 'react-qr-scanner';
import axios from 'axios';
import { API_URL } from '../api';

export default function Confirmacion() {
  const [mensaje, setMensaje] = useState('');
  const [tipoMensaje, setTipoMensaje] = useState(''); // ‚úÖ √©xito o error
  const [scanning, setScanning] = useState(true);
  const [autenticado, setAutenticado] = useState(false);
  const [credenciales, setCredenciales] = useState({ usuario: '', contrasena: '' });
  const manualRef = useRef();
  const ultimoEscaneo = useRef(''); // evita leer el mismo QR varias veces seguidas

  // üü¢ Escaneo del QR
  const handleScan = async (data) => {
    if (data && data.text) {
      const texto = data.text.trim();
      if (texto === ultimoEscaneo.current) return; // evita duplicados instant√°neos
      ultimoEscaneo.current = texto;
      const cedula = texto.replace('cedula:', '').trim();
      await confirmar(cedula);
    }
  };

  const handleError = (err) => {
    console.error('Error al escanear:', err);
  };

  // üü¢ Validar autenticaci√≥n local
  const login = () => {
    if (credenciales.usuario.trim() && credenciales.contrasena.trim()) {
      setAutenticado(true);
      setMensaje('');
    } else {
      setMensaje('‚ö†Ô∏è Debes ingresar usuario y contrase√±a');
    }
  };

  // üü¢ Confirmar asistencia (QR o manual)
  async function confirmar(cedula) {
    if (!cedula) return;
    setMensaje('');
    try {
      const cred = btoa(`${credenciales.usuario}:${credenciales.contrasena}`);
      const { data } = await axios.post(
        `${API_URL}/confirmar`,
        { cedula },
        { headers: { Authorization: `Basic ${cred}` } }
      );

      // ‚úÖ √âxito ‚Üí no pausar, solo mostrar notificaci√≥n temporal
      setTipoMensaje('exito');
      setMensaje(data.mensaje || '‚úÖ Asistencia confirmada');
      setTimeout(() => setMensaje(''), 1200); // se oculta en 1.2s
    } catch (err) {
      console.error(err);
      // ‚ùå Error ‚Üí pausar escaneo
      setTipoMensaje('error');
      setMensaje(err.response?.data?.error || '‚ùå Error confirmando asistencia');
      setScanning(false);
    }
  }

  // üü¢ Mostrar login si no est√° autenticado
  if (!autenticado) {
    return (
      <div style={{ padding: 20, textAlign: 'center', maxWidth: 400, margin: '0 auto' }}>
        <h2>Ingreso de Usuario</h2>
        <p>Ingresa tus credenciales para confirmar asistencia.</p>
        <input
          placeholder="Usuario"
          value={credenciales.usuario}
          onChange={(e) => setCredenciales({ ...credenciales, usuario: e.target.value })}
          style={{ width: '100%', padding: 6, marginBottom: 10 }}
        />
        <input
          placeholder="Contrase√±a"
          type="password"
          value={credenciales.contrasena}
          onChange={(e) => setCredenciales({ ...credenciales, contrasena: e.target.value })}
          style={{ width: '100%', padding: 6, marginBottom: 10 }}
        />
        <button
          onClick={login}
          style={{
            width: '100%',
            padding: '8px 12px',
            backgroundColor: '#007bff',
            color: 'white',
            border: 'none',
            borderRadius: '8px',
            cursor: 'pointer',
          }}
        >
          Ingresar
        </button>
        {mensaje && <p style={{ color: 'red', marginTop: 10 }}>{mensaje}</p>}
      </div>
    );
  }

  // üü¢ Vista principal de confirmaci√≥n
  return (
    <div style={{ padding: 20, textAlign: 'center' }}>
      <h2>Confirmar Asistencia</h2>
      <p>Escanea el c√≥digo QR del participante o introduce la c√©dula manualmente.</p>

      {scanning && (
        <div style={{ maxWidth: 400, margin: '0 auto' }}>
          <QrScanner
            delay={300}
            onError={handleError}
            onScan={handleScan}
            style={{ width: '100%' }}
          />
        </div>
      )}

      <div style={{ marginTop: 20 }}>
        <input
          placeholder="C√©dula (manual)"
          ref={manualRef}
          style={{ padding: 6, marginRight: 10 }}
        />
        <button
          onClick={() => confirmar(manualRef.current.value)}
          style={{ padding: '6px 12px', marginRight: 8 }}
        >
          Confirmar manual
        </button>
        <button
          onClick={() => {
            setScanning((s) => !s);
            setMensaje('');
          }}
          style={{ padding: '6px 12px' }}
        >
          {scanning ? '‚è∏Ô∏è Pausar escaneo' : '‚ñ∂Ô∏è Reanudar escaneo'}
        </button>
      </div>

      {/* üü¢ Notificaci√≥n flotante */}
      {mensaje && (
        <div
          style={{
            position: 'fixed',
            bottom: 30,
            left: '50%',
            transform: 'translateX(-50%)',
            backgroundColor: tipoMensaje === 'exito' ? '#28a745' : '#dc3545',
            color: 'white',
            padding: '10px 20px',
            borderRadius: 8,
            fontWeight: 'bold',
            boxShadow: '0 2px 8px rgba(0,0,0,0.2)',
            zIndex: 9999,
            transition: 'opacity 0.3s ease',
          }}
        >
          {mensaje}
        </div>
      )}
    </div>
  );
}
