import React, { useState, useRef } from 'react';
import QrScanner from 'react-qr-scanner';
import axios from 'axios';
import { API_URL } from '../api';

export default function Confirmacion() {
  const [mensaje, setMensaje] = useState('');
  const [scanning, setScanning] = useState(true);
  const manualRef = useRef();

  const handleScan = async (data) => {
    if (data && data.text) {
      const cedula = data.text.replace('cedula:', '').trim();
      await confirmar(cedula);
      setScanning(false);
    }
  };

  const handleError = (err) => {
    console.error('Error al escanear:', err);
  };

  // ✅ Única función confirmar (usa autenticación básica)
  const confirmar = async (cedula) => {
    if (!cedula) {
      setMensaje('⚠️ Ingresa o escanea una cédula válida');
      return;
    }

    setMensaje('⏳ Confirmando asistencia...');

    try {
      const credenciales = btoa('admin:changeme'); // codifica usuario:contraseña en Base64

      const { data } = await axios.post(
        `${API_URL}/confirmar`,
        { cedula },
        {
          headers: {
            'Authorization': `Basic ${credenciales}`,
          },
        }
      );

      setMensaje(data.mensaje || '✅ Asistencia confirmada');
    } catch (error) {
      console.error(error);
      setMensaje(
        error.response?.data?.error ||
          '❌ Error confirmando asistencia. Verifica el backend.'
      );
    }
  };

  return (
    <div style={{ padding: 20, textAlign: 'center' }}>
      <h2>Confirmar Asistencia</h2>
      <p>Escanea el código QR del participante o introduce la cédula manualmente.</p>

      {scanning && (
        <div style={{ maxWidth: 400, margin: '0 auto' }}>
          <QrScanner
            delay={300}
            onError={handleError}
            onScan={handleScan}
            style={{ width: '100%' }}
          />
        </div>
      )}

      <div style={{ marginTop: 20 }}>
        <input
          placeholder="Cédula (manual)"
          ref={manualRef}
          style={{ padding: 6, marginRight: 10 }}
        />
        <button
          onClick={() => confirmar(manualRef.current.value)}
          style={{ padding: '6px 12px', marginRight: 8 }}
        >
          Confirmar manual
        </button>
        <button
          onClick={() => {
            setScanning((s) => !s);
            setMensaje('');
          }}
          style={{ padding: '6px 12px' }}
        >
          {scanning ? '⏸️ Pausar escaneo' : '▶️ Reanudar escaneo'}
        </button>
      </div>

      {mensaje && (
        <p
          style={{
            marginTop: 20,
            fontWeight: 'bold',
            color: mensaje.startsWith('✅')
              ? 'green'
              : mensaje.startsWith('❌')
              ? 'red'
              : 'black',
          }}
        >
          {mensaje}
        </p>
      )}
    </div>
  );
}
