import React, { useState, useRef } from "react";
import { QrReader } from "react-qr-reader";
import axios from "axios";
import { API_URL } from "../api";

export default function Confirmacion() {
  const [mensaje, setMensaje] = useState("");
  const [tipoMensaje, setTipoMensaje] = useState("");
  const [autenticado, setAutenticado] = useState(false);
  const [credenciales, setCredenciales] = useState({ usuario: "", contrasena: "" });
  const manualRef = useRef();
  const ultimoEscaneo = useRef("");
  const bloqueado = useRef(false);

  // 🟢 Vibración + sonido
  const playFeedback = (success = true) => {
    const audioCtx = new (window.AudioContext || window.webkitAudioContext)();
    const oscillator = audioCtx.createOscillator();
    const gainNode = audioCtx.createGain();
    oscillator.connect(gainNode);
    gainNode.connect(audioCtx.destination);
    oscillator.type = success ? "sine" : "square";
    oscillator.frequency.setValueAtTime(success ? 880 : 220, audioCtx.currentTime);
    gainNode.gain.setValueAtTime(0.1, audioCtx.currentTime);
    oscillator.start();
    oscillator.stop(audioCtx.currentTime + 0.2);
    if (navigator.vibrate) navigator.vibrate(success ? [80, 30, 80] : [250]);
  };

  // 🟢 Login
  const login = async () => {
    if (!credenciales.usuario || !credenciales.contrasena) {
      setMensaje("⚠️ Debes ingresar usuario y contraseña");
      setTipoMensaje("error");
      return;
    }

    try {
      const cred = btoa(`${credenciales.usuario}:${credenciales.contrasena}`);
      await axios.post(`${API_URL}/login`, {}, { headers: { Authorization: `Basic ${cred}` } });
      setAutenticado(true);
      setMensaje("");
    } catch (error) {
      setTipoMensaje("error");
      if (error.response?.status === 401) {
        setMensaje("❌ Usuario o contraseña incorrectos");
      } else {
        setMensaje("⚠️ Error de conexión con el servidor");
      }
    }
  };

  const handleKeyDown = (e) => {
    if (e.key === "Enter") login();
  };

  // 🟢 Confirmar asistencia
  async function confirmar(cedula) {
    if (!cedula || bloqueado.current) return;

    bloqueado.current = true; // evita duplicados rápidos
    setMensaje("");

    try {
      const cred = btoa(`${credenciales.usuario}:${credenciales.contrasena}`);
      const { data } = await axios.post(
        `${API_URL}/confirmar`,
        { cedula },
        { headers: { Authorization: `Basic ${cred}` } }
      );

      playFeedback(true);
      setTipoMensaje("exito");
      setMensaje(data.mensaje || "✅ Asistencia confirmada");

      setTimeout(() => setMensaje(""), 800);
    } catch (err) {
      console.error("Error:", err);

      playFeedback(false);
      setTipoMensaje("error");
      if (err.response?.status === 404) setMensaje("❌ No se encontró la inscripción");
      else setMensaje("⚠️ Error confirmando asistencia");
    } finally {
      setTimeout(() => {
        bloqueado.current = false; // ✅ vuelve a permitir escanear
        ultimoEscaneo.current = "";
      }, 1000);
    }
  }

  // 🟢 Escaneo QR estable
  const handleScan = (data) => {
    if (data?.text) {
      const texto = data.text.trim();
      if (texto === ultimoEscaneo.current) return;
      ultimoEscaneo.current = texto;

      const cedula = texto.replace("cedula:", "").trim();
      confirmar(cedula);
    }
  };

  const handleError = (err) => console.error("Error QR:", err);

  // 🟢 Login UI
  if (!autenticado) {
    return (
      <div style={{ padding: 20, textAlign: "center", maxWidth: 400, margin: "0 auto" }}>
        <h2>Ingreso de Usuario</h2>
        <input
          placeholder="Usuario"
          value={credenciales.usuario}
          onChange={(e) => setCredenciales({ ...credenciales, usuario: e.target.value })}
          onKeyDown={handleKeyDown}
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <input
          type="password"
          placeholder="Contraseña"
          value={credenciales.contrasena}
          onChange={(e) => setCredenciales({ ...credenciales, contrasena: e.target.value })}
          onKeyDown={handleKeyDown}
          style={{ width: "100%", padding: 8, marginBottom: 10 }}
        />
        <button
          onClick={login}
          style={{
            width: "100%",
            padding: "10px 12px",
            backgroundColor: "#007bff",
            color: "white",
            border: "none",
            borderRadius: "8px",
            cursor: "pointer",
          }}
        >
          Ingresar
        </button>
        {mensaje && <p style={{ color: "red", marginTop: 10 }}>{mensaje}</p>}
      </div>
    );
  }

  // 🟢 Vista principal
  return (
    <div style={{ padding: 20, textAlign: "center" }}>
      <h2>Confirmar Asistencia</h2>
      <p>Escanea el QR o ingresa la cédula manualmente.</p>

      <div style={{ maxWidth: 400, margin: "0 auto" }}>
        <QrReader
          constraints={{ facingMode: "environment" }}
          onResult={(result, error) => {
            if (!!result) handleScan(result);
            if (!!error) handleError(error);
          }}
          style={{ width: "100%" }}
        />
      </div>

      <div style={{ marginTop: 20 }}>
        <input placeholder="Cédula (manual)" ref={manualRef} style={{ padding: 6, marginRight: 10 }} />
        <button onClick={() => confirmar(manualRef.current.value)} style={{ padding: "6px 12px" }}>
          Confirmar manual
        </button>
      </div>

      {mensaje && (
  <div
    style={{
      position: "fixed",
      top: 0,
      left: 0,
      right: 0,
      bottom: 0,
      backgroundColor: "rgba(0,0,0,0.5)",
      display: "flex",
      alignItems: "center",
      justifyContent: "center",
      zIndex: 9999,
    }}
  >
    <div
      style={{
        backgroundColor: "white",
        borderRadius: 16,
        padding: "40px 60px",
        textAlign: "center",
        boxShadow: "0 4px 20px rgba(0,0,0,0.3)",
      }}
    >
      <div style={{ fontSize: 80 }}>{tipoMensaje === "exito" ? "✅" : "❌"}</div>
      <h3
        style={{
          marginTop: 10,
          color: tipoMensaje === "exito" ? "#28a745" : "#dc3545",
        }}
      >
        {mensaje}
          </h3>

          {/* 🟢 Botón Cerrar */}
          <button
            onClick={() => {
              setMensaje("");
              setTipoMensaje("");
            }}
            style={{
              marginTop: 20,
              padding: "10px 20px",
              borderRadius: 10,
              backgroundColor: "#007bff",
              color: "white",
              border: "none",
              fontWeight: "bold",
              cursor: "pointer",
              boxShadow: "0 2px 6px rgba(0,0,0,0.2)",
              transition: "0.2s",
            }}
            onMouseOver={(e) => (e.target.style.backgroundColor = "#0056b3")}
            onMouseOut={(e) => (e.target.style.backgroundColor = "#007bff")}
          >
            Cerrar
          </button>
        </div>
      </div>
    )}
    </div>
  );
}
