import React, { useState } from 'react';
import axios from 'axios';
import { API_URL } from '../api';

export default function Registro() {
  const [form, setForm] = useState({
    nombre: '',
    cedula: '',
    email: '',
    cargo: '',
    entidad: ''
  });
  const [qr, setQr] = useState(null);
  const [mensaje, setMensaje] = useState('');

  const handleChange = (e) => setForm({ ...form, [e.target.name]: e.target.value });

  const handleSubmit = async (e) => {
    e.preventDefault();
    setMensaje('');
    setQr(null);
    try {
      const { data } = await axios.post(`${API_URL}/inscripcion`, form);
      setQr(data.qrCode);
      setMensaje(data.mensaje || 'Inscripci贸n registrada');
    } catch (error) {
      if (error.response?.status === 409) {
        alert(error.response.data.error); // Muestra el mensaje "Esta c茅dula ya est谩 registrada"
      } else {
        alert("Ocurri贸 un error inesperado al registrar la inscripci贸n.");
      }
    }
  };

  return (
    <div>
      <h2>Registro al Evento</h2>
      <form onSubmit={handleSubmit}>
        <input name="nombre" placeholder="Nombre completo" value={form.nombre} onChange={handleChange} required />
        <input name="cedula" placeholder="C茅dula" value={form.cedula} onChange={handleChange} required />
        <input name="email" placeholder="Email" type="email" value={form.email} onChange={handleChange} required />
        <input name="cargo" placeholder="Cargo / Rol" value={form.cargo} onChange={handleChange} />
        <input name="entidad" placeholder="Entidad / Empresa" value={form.entidad} onChange={handleChange} />
        <button type="submit">Registrar</button>
      </form>

      {mensaje && <p><strong>{mensaje}</strong></p>}

      {qr && (
        <div>
          <h3>QR generado (descarga o guarda imagen):</h3>
          <img
            src={qr}
            alt="QR de confirmaci贸n"
            style={{
              width: '100%',         //  ocupa todo el ancho del contenedor
              height: 'auto',        // mantiene la proporci贸n
              maxWidth: '600px',     // l铆mite opcional para que no sea exagerado
              border: '2px solid #ccc',
              borderRadius: '2px',
              padding: '2px',
              backgroundColor: 'white',
              display: 'block',
              margin: '0 auto'
            }}
          />
          <button
            onClick={() => {
              const link = document.createElement('a');
              const nombreArchivo = `qr_${form.nombre.replace(/\s+/g, '_')}_${form.cedula}.png`; //  genera el nombre
              link.href = qr;
              link.download = nombreArchivo;
              document.body.appendChild(link);
              link.click();
              document.body.removeChild(link);
            }}
            style={{
              marginTop: '15px',
              backgroundColor: '#007bff',
              color: 'white',
              border: 'none',
              borderRadius: '8px',
              padding: '10px 20px',
              cursor: 'pointer',
              fontSize: '16px'
            }}
            >
             Descargar QR
          </button>
        </div>
      )}
    </div>
  );
}
